code.function:
arguments.callee.grammar.call(this, id, value);
if(id=="DO_ABOUT_FUNCTION"){
	if(value=="doNext")
		this.ABOUT.doNext()
	else if(value=="doPrevious")
		this.ABOUT.doPrevious()
	else if(value=="pageChanged")
		this.ABOUT.pageChanged()
}

var c = this.countPages();
var i = this.getPage() + 1;
if (!c) c++;
this.setVariable("ABOUT_INDEX_COUNT", i + "fskin:/l/strings/STR_UI_PARTS_OF".idToString() + c);
this.checkStrage();
